import { Component } from '@angular/core';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.scss']
})
export class HomePageComponent {

}
<section id="hero" class="d-flex align-items-center">
  <div class="container position-relative" data-aos="fade-up" data-aos-delay="500">
    <form id="heroForm" >
      <h1>Welcome to 321 Movies</h1>
      <h2>Unlimited Entertainment at your fingertips</h2>
      <button ID="Login"  class="btn-get-started" OnClick="btn_UserLogin" >Sign up</button>
      <button ID="Signin"  class="btn-get-started" OnClick="btn_UserSignIn"  >Sign In</button>
    </form>
  </div>
</section>
